Base64UrlEncode<pre><code>private static string Base64UrlEncode(string someString)
    {
        var bytes = Encoding.UTF8.GetBytes(someString);
        return Base64UrlEncode(bytes);
    }   

private static string Base64UrlEncode(byte[] bytes)
    {
        var base64 = System.Convert.ToBase64String(bytes);
        var base64Url = base64.TrimEnd('=').Replace('+', '-').Replace('/', '_');
        return base64Url;
    }
</code></pre> Base64UrlDecode<pre><code> public static string Base64UrlDecode(string encodedString)
   {
       string incoming = encodedString
           .Replace('_', '/').Replace('-', '+');
       switch (incoming.Length % 4)
       {
           case 2: incoming += "=="; break;
           case 3: incoming += "="; break;
       }

       byte[] bytes = Convert.FromBase64String(incoming);
       string originalText = Encoding.ASCII.GetString(bytes);
       return originalText;
   }
